const AddMessageForm = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                    <textarea value={state.newMessageBody}
                              onChange={onNewMessageChange}
                              placeholder="Enter your message"></textarea>
            </div>
            <div>
                <button onClick={() => {
                    onSendMessageClick()
                }}>Send
                </button>
            </div>
        </form>
    )
}

export default AddMessageForm;
