const UPDATE_NEW_MESSAGE_BODY = 'UPDATE-NEW-MESSAGE-BODY';
const SEND_MESSAGE = 'SEND_MESSAGE';


let store = {
    _state: {
        profilePage: {
            posts:  [
                {id: 1, massage: 'Hi, how are you?', likeCount: 0},
                {id: 2, massage: 'It\'s my first post!', likeCount: 24},
            ],
            NewPostText: ''
        },
        messagesPage: {
            dialogs: [
                {id: 1, name: 'Dimych'},
                {id: 2, name: 'Andrey'},
                {id: 3, name: 'Sveta'},
                {id: 4, name: 'Sasha'},
                {id: 5, name: 'Victor'},
                {id: 6, name: 'Valera'}
            ],
            messages: [
                {id: 1, massage: 'Hi'},
                {id: 2, massage: 'How are u'},
                {id: 3, massage: 'Yo'},
                {id: 4, massage: 'Yo'},
                {id: 5, massage: 'You'},
                {id: 6, massage: 'yp'},
            ],
            newMessageBody: ""
        },
        sitebar: {

        }
    },
    _callSubscriber () {
        console.log('State change')
    },

    getState(){
        return this._state
    },
    subscribe (observe) {
        this._callSubscriber = observe;
    },

    dispatch(action) {
        if (action.type === ADD_POST) {
            let newPost = {
                id: 5,
                massage: this._state.profilePage.NewPostText,
                likeCount: 0,
            };
            this._state.profilePage.posts.push(newPost);
            this._state.profilePage.NewPostText = '';
            this._callSubscriber(this._state);
        } else if (action.type === UPDATE_NEW_POST_TEXT) {
            this._state.profilePage.NewPostText = action.NewText;
            this._callSubscriber(this._state);
        } else if (action.type === UPDATE_NEW_MESSAGE_BODY) {
            this._state.messagesPage.newMessageBody = action.body;
            this._callSubscriber(this._state);
        } else if (action.type === SEND_MESSAGE) {
            let body = this._state.messagesPage.newMessageBody;
            this._state.messagesPage.newMessageBody = '';
            this._state.messagesPage.messages.push({
                id: 6,
                massage: body},
                );
            this._callSubscriber(this._state);
        }

    }
}
export const addPostActionCreate = () => ({type: ADD_POST,})
export const updateNewPostTextActionCreate = (text) => {
    return {
        type: UPDATE_NEW_POST_TEXT,
        NewText: text,
    }
}

export const sendMessageCreate = () => ({type: SEND_MESSAGE,})
export const updateNewMessageBodyCreate = (body) => {
    return {
        type: UPDATE_NEW_MESSAGE_BODY,
        body: body,
    }
}

export default store;
window.store = store;
