let store = {
    state: {
        profilePage: {
            posts:  [
                {id: 1, massage: 'Hi, how are you?', likeCount: 0},
                {id: 2, massage: 'It\'s my first post!', likeCount: 24},
            ],
            NewPostText: 'Лох'
        },
        messagesPage: {
            dialogs: [
                {id: 1, name: 'Dimych'},
                {id: 2, name: 'Andrey'},
                {id: 3, name: 'Sveta'},
                {id: 4, name: 'Sasha'},
                {id: 5, name: 'Victor'},
                {id: 6, name: 'Valera'}
            ],
            messages: [
                {id: 1, massage: 'Hi'},
                {id: 2, massage: 'How are u'},
                {id: 3, massage: 'Yo'},
                {id: 4, massage: 'Yo'},
                {id: 5, massage: 'You'},
                {id: 6, massage: 'yp'},
            ],
        },
        sitebar: {

        }
    },
    rerenderEntireTree () {
        console.log('State change')
    },
    addPost () {
        let newPost = {
            id: 5,
            massage: state.profilePage.NewPostText,
            likeCount: 0,
        };
        state.profilePage.posts.push(newPost);
        state.profilePage.NewPostText = '';
        rerenderEntireTree(state);
    },
    updateNewPostText (text) {
        state.profilePage.NewPostText = text;
        rerenderEntireTree(state);
    },
    subscribe (observe) {
        rerenderEntireTree = observe;
    }
}

export default store;
